import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  return <ParentContainer />;
}

function TextField({ text, onType }) {
  const [nodeValue, setNodeValue] = useState("");

  useEffect(() => {
    setNodeValue(text);
  }, [text]);

  function getText(e) {
    const val = e.target.value;
    setNodeValue(val);
    onType(val); //pass data to parent
  }

  return (
    <textarea
      style={{ width: "80%", height: "5em", margin: "1em 0" }}
      value={nodeValue}
      onChange={getText}
      placeholder="Image description..."
    ></textarea>
  );
}

function Submit({ data, onClick }) {
  return (
    <button
      style={{ padding: "1em", width: "80%" }}
      onClick={() => onClick(data)}
    >
      Submit
    </button>
  );
}

function ImgContainer({ src, onResponse }) {
  const [imgSrc, setImgSrc] = useState("");

  useEffect(() => {
    setImgSrc(src);
  }, [src]);

  return (
    <div style={{ width: "80%", height: "50%", border: "1px solid black" }}>
      <img style={{ width: "100%", height: "100%" }} src={src}></img>
    </div>
  );
}

function ParentContainer() {
  const [sharedUserMessage, setSharedUserMessage] = useState("");
  const [sharedImgSrc, setSharedImgSrc] = useState("");

  async function urlToBase64(url) {
    try {
      const response = await fetch(url);
      const blob = await response.blob();
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result);
        reader.onerror = reject;
        reader.readAsDataURL(blob);
      });
    } catch (error) {
      console.error("Error converting URL to base64:", error);
      return null;
    }
  }

  async function exportText(text) {
    const params = { message: text };
    let base64;

    try {
      // const response = await axios.get("http://localhost:8080/generateImage", {
      //   params,
      // });
      const response = {
        data: {
          url: await fetch(`https://cors-anywhere.herokuapp.com/${url}`, {
            headers: {
              Origin: "http://localhost:3000", // Set the origin header to bypass CORS Anywhere's restrictions
            },
          }),
        },
      };

      urlToBase64(response.data.url)
        .then((base64String) => {
          console.log("Base64 string:", base64String);
          // Use the base64String as needed (e.g., set as image source)
        })
        .catch((error) => {
          console.error("Error:", error);
        });
    } catch (error) {
      setSharedUserMessage(error);
    }
  }

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        width: "100vw",
        height: "100vh",
      }}
    >
      <h1>Image Generator</h1>
      <ImgContainer src={sharedImgSrc} onResponse={setSharedImgSrc} />
      <TextField text={sharedUserMessage} onType={setSharedUserMessage} />{" "}
      <Submit data={sharedUserMessage} onClick={exportText} />
    </div>
  );
}

export default App;
