import "./App.css";
import { useState, useEffect, useRef } from "react";
import axios from "axios";

function App() {
  return (
    <div style={{ width: "100%", height: "100%" }}>
      <Container />
    </div>
  );
}

function TextField({ text, onType }) {
  const [nodeValue, setNodeValue] = useState("");

  useEffect(() => {
    setNodeValue(text);
  }, [text]);

  const handleChange = (e) => {
    if (e.key === "Enter") {
      debugger;
    }
  };

  function getText(e) {
    const val = e.target.value;
    setNodeValue(val);
    onType(val); //pass data to parent
  }

  return (
    <textarea
      style={{ width: "80%", height: "300px" }}
      value={nodeValue}
      onChange={getText}
      onKeyPress={handleChange}
    ></textarea>
  );
}

function Submit({ data, onClick }) {
  return (
    <button
      style={{ marginTop: "1em", padding: "1em", width: "80%" }}
      onClick={() => onClick(data)}
    >
      Submit
    </button>
  );
}

function Container() {
  const [sharedUserMessage, setSharedUserMessage] = useState("");
  const submitButtonRef = useRef(null);

  const dataChange = function (newData) {
    setSharedUserMessage(newData);
  };

  useEffect(() => {
    if (submitButtonRef.current) submitButtonRef.current.click();
  }, sharedUserMessage);

  async function exportText(text) {
    const params = { message: text };

    try {
      const response = await axios.get("http://localhost:8080/generateImage", {
        params,
      });
      setSharedUserMessage(response.data.message);
    } catch (error) {
      console.error("Error:", error);
    }
  }

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <h1>Image Generator</h1>
      <TextField text={sharedUserMessage} onType={dataChange} />{" "}
      <Submit
        ref={submitButtonRef}
        data={sharedUserMessage}
        onClick={exportText}
      />
    </div>
  );
}

export default App;
